const DetailItem = ({
  title,
  value,
  svg,
}: {
  title: string;
  value: string | number;
  svg: React.ReactNode;
}) => {
  return (
    <div
      className={
        'border-2 rounded-md border-primary flex flex-col items-center justify-center p-4'
      }
    >
      <span className={'flex items-center gap-2'}>
        <div>{svg}</div>
        <div>
          <div className={'text-sm text-muted font-medium -mb-1'}>{title}</div>
          <div className={'text-primary text-lg font-semibold'}>{value}</div>
        </div>
      </span>
    </div>
  );
};

